%Thanakorn Pasangthien 6088109%root_method1x = 0; %qpproximation answerprevious_result = x;printf("The square root of 6 is %f \n ",sqrt(6))%first stepfor i = 1:10    x = previous_result + i;    if(x * x > 6)      break;    endifendforx = x-1;printf("first digit = %f\n",x);previous_result = x;%second stepfor i = 1:10    x = previous_result + i/10;    if(x * x > 6)      break;    endifendforx = x-0.1;printf("second digit = %f\n",x);previous_result = x;%third stepfor i = 1:10    x = previous_result + i/100;    if(x * x > 6)      break;    endifendforx = x-0.01;printf("third digit = %f\n",x);previous_result = x;%fourth stepfor i = 1:10    x = previous_result + i/1000;    if(x * x > 6)      break;    endifendforx = x-0.001;printf("fourth digit = %f\n",x);previous_result = x;  %fifth stepfor i = 1:10    x = previous_result + i/10000;    if(x * x > 6)      break;    endifendforx = x-0.0001;printf("fifth digit = %f\n",x);previous_result = x;