%Thanakorn Pasangthien 6088109%take square root of 6target = 6;%set approximation answerx = 0previous_result = x;%set the amount of digit need to be testedn = 6;printf("The first method to find square root\n")for d = 0:n    previous_result = x;    for j = 1:10      x = previous_result + j/(10^d);      if(x * x > target)        break;      endif    endfor    x = x - 1/(10^d);endforprintf("the square root of 6 = %f\n",x);