%Thanakorn Pasangthien 6088109%Taylor Expansion methodnf = 6;es = 0.5 * 10^(2-nf);ea = 100;x = 0.5;sumx = 0;printf("The third method to find square root of 6 = %f\n",sqrt(6));i = 0;while(abs(ea) > es)  pre_sumx = sumx;  syms a;  f = (1+a)^0.5;  k = diff(f,a,i);  temp_diff = double(subs(k,'a',0));  sumx = sumx + (temp_diff/factorial(i))*(0.5^i);  ea = ((sumx - pre_sumx)/sumx)*100;  printf("sumx = %f, ea = %f ",sumx,ea);  i++;endwhileresult = 2*sumx;truncation = 2.44948974278318 - double(result); printf("\nthe square root of 6 = %f\n", result);printf("truncation error is = %.15f",truncation); 